import axios from "axios";
import { useEffect, useState } from "react";
import { Runner, Stage } from "./BeforeRace";

type CurrentRunner = {
  runner: Runner;
  stage: Stage;
  scheduleDifference: number;
};

const AfterStart = () => {
  const [currentRunner, setCurrentRunner] = useState<CurrentRunner | null>(
    null
  );

  const updateCurrentRunner = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("/api/v1/currentRunner", {
        headers: { Authorization: "Bearer " + token },
      });
      setCurrentRunner(res.data as CurrentRunner);
    } catch (err) {
      alert("Unknown error occured.");
      window.location.reload();
    }
  };

  useEffect(() => {
    updateCurrentRunner();

    setInterval(async () => {
      await updateCurrentRunner();
    }, 5000);
  }, []);

  if (currentRunner === null) return null;

  return (
    <>
      <span className="font-bold text-lg">Current runner</span>
      <div className="w-full bg-gradient-to-r from-pink to-purple-2 rounded-2xl flex flex-col px-6 text-white py-5">
        <span className="text-xl font-medium">
          <i className="far fa-user"></i> {currentRunner.runner.firstName}{" "}
          {currentRunner.runner.lastName}
        </span>
      </div>
    </>
  );
};

export default AfterStart;
