import axios from "axios";
import { useEffect, useState } from "react";
import { Runner, Stage } from "./BeforeRace";

type CurrentRunner = {
  runner: Runner;
  stage: Stage;
  scheduleDifference: number;
};

const AfterStart = () => {
  const [currentRunner, setCurrentRunner] = useState<CurrentRunner | null>(
    null
  );

  const updateCurrentRunner = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("/api/v1/currentRunner", {
        headers: { Authorization: "Bearer " + token },
      });
      setCurrentRunner(res.data as CurrentRunner);
    } catch (err) {
      alert("Unknown error occured.");
      window.location.reload();
    }
  };

  useEffect(() => {
    updateCurrentRunner();

    setInterval(async () => {
      await updateCurrentRunner();
    }, 5000);
  }, []);

  if (currentRunner === null) return null;

  const scheduleAhead = currentRunner.scheduleDifference < 0;
  const scheduleDifference = Math.abs(currentRunner.scheduleDifference);
  const scheduleDiffHours = Math.floor(scheduleDifference / 60 / 60);
  const scheduleDiffMinutes = Math.floor((scheduleDifference / 60) % 60);
  const scheduleDiffSeconds = Math.ceil(scheduleDifference % 60);

  return (
    <>
      <span className="font-bold text-lg">Current runner</span>
      <div className="w-full bg-gradient-to-r from-pink to-purple-2 rounded-2xl flex flex-col px-6 text-white py-5 space-y-3">
        <span className="text-xl font-medium mr-3">
          <i className="far fa-user"></i> {currentRunner.runner.firstName}{" "}
          {currentRunner.runner.lastName}
        </span>
        <div className="flex items-center">
          <span className="text-xl font-medium mr-3">
            <i className="far fa-map"></i> {/* missing map-marker icon */}
          </span>
          <div className="flex flex-col mr-4">
            <span>{currentRunner.stage.startingLocation}</span>
            <span>{currentRunner.stage.arrivalLocation}</span>
          </div>
          <span className="border-l border-white pl-3">
            {currentRunner.stage.distance} km
          </span>
        </div>
        <div className="flex items-center">
          <span className="text-xl font-medium mr-2">
            <i className="far fa-clock"></i>
          </span>
          <div className="flex flex-col">
            <span className="text-xl"></span>
          </div>
        </div>
      </div>
    </>
  );
};

export default AfterStart;
