import useData from "../common/hooks/useData";

const BeforeStart = () => {
  const { user, timeUntilEvent } = useData();

  const daysToGo = Math.ceil(timeUntilEvent / 1000 / 60 / 60 / 24);
  const firstDigit = daysToGo < 10 ? 0 : daysToGo.toString()[0];
  const secondDigit = daysToGo.toString()[1];

  return (
    <div className="w-full h-[95vw] flex flex-col">
      <h1 className="text-3xl font-bold mt-8">ðŸ‘‹ Hi {user?.firstName}!</h1>
      <h2 className="mt-1">Have a good running!</h2>

      <div className="flex items-center mt-6">
        <span className="px-3 py-3 bg-blue-1 rounded-xl text-white font-bold text-5xl mr-2">
          {firstDigit}
        </span>
        <span className="px-3 py-3 bg-blue-1 rounded-xl text-white font-bold text-5xl mr-6">
          {secondDigit}
        </span>
        <span className="text-3xl text-blue-1 font-bold">Days to go!</span>
      </div>

      <img className="my-auto" src="images/runner.svg" alt="runner" />
    </div>
  );
};

export default BeforeStart;
