import { Runner } from ".";

type Props = {
  runner: Runner;
};

const TableRow: React.FC<Props> = ({ runner }) => {
  const handleCopy = async () => {
    await navigator.clipboard.writeText();
  };

  return (
    <div className="flex">
      <div className="flex-1 border-r border-gray-500 px-2 py-1">
        {runner.firstName}
      </div>
      <div className="flex-1 border-r border-gray-500 px-2 py-1">
        {runner.lastName}
      </div>
      <div className="flex-1 border-r border-gray-500 px-2 py-1">
        {runner.speed}
      </div>
      <div className="flex-1 border-r border-gray-500 px-2 py-1">
        {runner.token}
      </div>

      <div className="w-[10vw] flex justify-between items-center px-4">
        <button className="border text-sm rounded-md bg-gray-200 border-gray-500 px-1">
          Copy
        </button>
        <button className="border text-sm rounded-md bg-green-200 border-green-500 px-1">
          Save
        </button>
        <button className="border text-sm rounded-md bg-red-200 border-red-500 px-1">
          Delete
        </button>
      </div>
    </div>
  );
};

export default TableRow;
