import React from "react";
import { Runner } from ".";

type Props = {
  runner: Runner;
};

const TableRow: React.FC<Props> = ({ runner }) => {
  const handleCopy = async () => {
    await navigator.clipboard.writeText(runner.token);
  };

  const handleSave = async () => {};

  const [firstName, setFirstName] = React.useState<string>(runner.firstName);
  const firstNameChanged = (e: React.FormEvent<HTMLInputElement>) => {
    setFirstName(e.currentTarget.value);
  };

  const [lastName, setLastName] = React.useState<string>(runner.lastName);
  const [speed, setSpeed] = React.useState<string>(runner.speed);
  const [token, setToken] = React.useState<string>(runner.token);

  return (
    <div className="flex">
      <input
        type="text"
        className="flex-1 border-r border-gray-500 px-2 py-1"
        value={firstName}
      />
      <input
        type="text"
        className="flex-1 border-r border-gray-500 px-2 py-1"
        value={lastName}
      />
      <input
        type="text"
        className="flex-1 border-r border-gray-500 px-2 py-1"
        value={speed}
      />
      <input
        type="text"
        className="flex-1 border-r border-gray-500 px-2 py-1"
        value={token}
      />

      <div className="w-[10vw] flex justify-between items-center px-4">
        <button
          className="border text-sm rounded-md bg-gray-200 border-gray-500 px-1"
          onClick={handleCopy}
        >
          Copy
        </button>
        <button className="border text-sm rounded-md bg-green-200 border-green-500 px-1">
          Save
        </button>
        <button className="border text-sm rounded-md bg-red-200 border-red-500 px-1">
          Delete
        </button>
      </div>
    </div>
  );
};

export default TableRow;
