import axios from "axios";
import React from "react";
import ManageTeam from "./ManageTeam";
import RunnersTable from "./RunnersTable/index";

const Home = () => {
  const [loggedIn, setLoggedIn] = React.useState<boolean>(false);

  const checkIfLoggedIn = async () => {
    const savedToken = localStorage.getItem("token");
    if (!savedToken) {
      window.location.replace("/login");
    }

    try {
      await axios.post("/api/v1/login", { token: savedToken });
      setLoggedIn(true);
    } catch (err) {
      alert("The token is incorrect. Please log in.");
      localStorage.removeItem("token");
      window.location.replace("/login");
    }
  };

  React.useEffect(() => {
    checkIfLoggedIn();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.replace("/login");
  };

  if (!loggedIn)
    return (
      <main className="min-w-screen min-h-screen flex justify-center items-center text-3xl">
        Loading...
      </main>
    );

  return (
    <main className="min-h-screen w-[95vw] border border-gray-400 rounded-lg mx-auto flex flex-col px-8 py-6">
      <div className="flex justify-between w-full items-start">
        <ManageTeam />
        <button
          className="px-2 py-1 rounded-md bg-gray-200 border border-gray-500 font-medium"
          onClick={handleLogout}
        >
          Logout
        </button>
      </div>
      <hr className="my-6" />
      <RunnersTable />
    </main>
  );
};

export default Home;
