import axios from "axios";

type Props = {
  teamId: number;
};

const ConfirmationWindow: React.FC<Props> = ({ teamId }) => {
  const handleDelete = async () => {
    const token = localStorage.getItem("token");
    try {
      await axios.delete(`/api/v1/teams/${teamId}`, {
        headers: { Authorization: "Bearer " + token },
      });

      alert("Csapat törölve.");

      localStorage.removeItem("token");
      window.location.replace("/login");
    } catch (err) {
      alert("Hiba történt.");
    }
  };

  return (
    <div className="flex flex-col max-w-[20vw] justify-center items-center z-10 border border-gray-500 rounded-md px-4 py-2 bg-opacity-10">
      <h1 className="text-2xl font-medium">Are you sure?</h1>
      <p className="mt-2 text-sm text-center mb-6">
        If you click on the delete button, your team and all runners belonging
        to your team will be deleted.
      </p>
      <div className="flex justify-between w-full px-6">
        <button>Cancel</button>
        <button className="border border-gray-500 bg-gray-200">Delete</button>
      </div>
    </div>
  );
};

export default ConfirmationWindow;
