import axios from "axios";
import React from "react";

const Home = () => {
  const checkIfLoggedIn = async () => {
    const savedToken = localStorage.getItem("token");
    if (!savedToken) {
      window.location.replace("/login");
    }

    try {
      await axios.post("/api/v1/login", { token: savedToken });
    } catch (err) {
      alert("The token is incorrect. Please log in.");
      localStorage.removeItem("token");
      window.location.replace("/login");
    }
  };

  React.useEffect(() => {
    checkIfLoggedIn();
  }, []);

  return (
    <main className="min-h-screen min-w-screen mx-12 border border-gray-400 rounded-xl">
      asd
    </main>
  );
};

export default Home;
